import React, {
  createContext,
  useCallback,
  useContext,
  useMemo,
  useState
} from 'react'

declare type EntitiesType = 'pokemons'
declare interface EntitiesOBJType {
  [key: EntitiesType | string]: unknown[] | unknown
}

interface PageContextTypes {
  entities: EntitiesOBJType
  dispatchData: (entity: EntitiesType, data: unknown[] | unknown) => void
}

const PageContext = createContext({} as PageContextTypes)

export const usePage = () => useContext(PageContext)
export const PageContextWrapper: React.FC = ({ children }) => {
  const [entities, setEntities] = useState<EntitiesOBJType>({})

  // Set entities value
  const dispatchData = useCallback((entity: EntitiesType, data: unknown[]) => {
    if (entities[entity].length > 0) {
      return setEntities((cur) => ({
        ...cur,
        [entity]: [...entities[entity], ...data]
      }))
    }
    return setEntities((cur) => ({
      ...cur,
      [entity]: data
    }))
  }, [])

  const ctxValue = useMemo(
    () => ({
      dispatchData,
      entities
    }),
    [dispatchData, entities]
  )
  return (
    <PageContext.Provider value={ctxValue}>{children}</PageContext.Provider>
  )
}
