import React, { createContext, useContext, useMemo, useState } from 'react'
import { queryPokemons } from '../../hooks/query'
import {
  PaginatedResponse,
  QueryPokemonsTypes,
  ReturnOBJ
} from '../../hooks/query/queryPokemons'

interface PokemonsContextTypes {
  paginated: () => {
    data: {
      paginatedData?: PaginatedResponse
      page: number
    }
    actions: {
      getPaginated: (pageParam?: number | undefined) => void
      nextPage: () => void
      previousPage: () => void
    }
  }
  all: () => {
    data?: ReturnOBJ[]
    actions: {
      getAll: () => void
    }
  }
}
const PokemonsContext = createContext({} as PokemonsContextTypes)

export const usePokemons = () => useContext(PokemonsContext)
export const PokemonContextWrapper: React.FC = ({ children }) => {
  const [allData, setAll] = useState<ReturnOBJ[]>([])
  const [paginatedData, setPaginated] = useState<PaginatedResponse>()

  const [page, setPage] = useState<number>(1)

  const { actions, data } = queryPokemons({
    allData,
    paginatedData,
    setAll,
    page,
    setPage,
    setPaginated
  })

  const runPaginated = () => {
    actions.paginated.getPaginated()
    return {
      data: paginatedData,
      actions: actions.paginated
    }
  }

  const runAll = () => {
    actions.paginated.getPaginated()
    return {
      data: allData,
      actions: actions.all
    }
  }

  const ctxValue: PokemonsContextTypes = useMemo(
    () => ({
      paginated: runPaginated,
      all: runAll
    }),
    [actions, data]
  )

  return (
    <PokemonsContext.Provider value={ctxValue}>
      {children}
    </PokemonsContext.Provider>
  )
}
