import React, {
  createContext,
  useCallback,
  useContext,
  useEffect,
  useMemo,
  useState
} from 'react'
import { queryPokemons } from '../../hooks/query'
import { PaginatedResponse, ReturnOBJ } from '../../hooks/query/queryPokemons'

interface PokemonsContextTypes {
  paginated: () => {
    data?: PaginatedResponse
    actions: {
      nextPage: () => void
      previousPage: () => void
    }
  }
  all: () => {
    data?: ReturnOBJ[]
  }
}
const PokemonsContext = createContext({} as PokemonsContextTypes)

export const usePokemons = () => useContext(PokemonsContext)
export const PokemonContextWrapper: React.FC = ({ children }) => {
  const [allData, setAll] = useState<ReturnOBJ[]>([])
  const [paginatedData, setPaginated] = useState<PaginatedResponse>()
  const [page, setPage] = useState<number>(1)

  const { getPaginated, getAll } = queryPokemons()

  const nextPage = () => {
    setPage(page + 1)
  }
  const previousPage = () => {
    setPage(page - 1)
  }

  const runPaginated = useCallback(() => {
    const { data } = getPaginated(page)
    if (data) setPaginated(data)

    return {
      data,
      actions: { nextPage, previousPage }
    }
  }, [])

  const runAll = useCallback(() => {
    const { data } = getAll()
    if (data) setAll(data)

    return {
      data
    }
  }, [])

  useEffect(() => {
    runPaginated()
  }, [page])

  const ctxValue: PokemonsContextTypes = useMemo(
    () => ({
      paginated: runPaginated,
      all: runAll
    }),
    [runAll, runPaginated, paginatedData]
  )

  return (
    <PokemonsContext.Provider value={ctxValue}>
      {children}
    </PokemonsContext.Provider>
  )
}
