import React, {
  createContext,
  useCallback,
  useContext,
  useEffect,
  useMemo,
  useState
} from 'react'
import { queryPokemons } from '../../hooks/query'
import { PaginatedResponse, ReturnOBJ } from '../../hooks/query/queryPokemons'

declare type EntitiesType = 'pokemons' | string
declare type EntitiesOBJType = { [key: EntitiesType]: unknown[] }

interface PokemonsContextTypes {
  entities: EntitiesOBJType
}
const PokemonsContext = createContext({} as PokemonsContextTypes)

export const usePokemons = () => useContext(PokemonsContext)
export const PokemonContextWrapper: React.FC = ({ children }) => {
  const [entities, setEntities] = useState<EntitiesOBJType>({})

  const dispatchData = (entity: EntitiesType, data: unknown[]) => {
    if (entities[entity].length > 0) {
      return setEntities((cur) => ({
        ...cur,
        [entity]: [...entities[entity], ...data]
      }))
    }
    return setEntities((cur) => ({
      ...cur,
      [entity]: data
    }))
  }
  const ctxValue = useMemo(
    () => ({
      dispatchData,
      entities
    }),
    [dispatchData, entities]
  )
  return (
    <PokemonsContext.Provider value={ctxValue}>
      {children}
    </PokemonsContext.Provider>
  )
}
