import React, { useState } from 'react'
import { useQuery } from 'react-query'
import { api } from '../../service/api'

export type ReturnOBJ = {
  name: string
  url: string
}

export interface PaginatedResponse {
  count: number
  next?: string
  previous?: string
  results: ReturnOBJ[]
}

export interface QueryPokemonsTypes {
  data: {
    allData?: ReturnOBJ[]
    paginated: {
      paginatedData?: PaginatedResponse
      page: number
    }
  }
  actions: {
    paginated: {
      getPaginated: (pageParam?: number) => void
      nextPage: () => void
      previousPage: () => void
    }

    all: {
      getAll: () => void
    }
  }
}

export const queryPokemons = () => {
  const getAll = () => {
    const { data, error } = useQuery(
      'pokemons/all',
      () =>
        api
          .get<PaginatedResponse>(`/pokemon/?limit=500&offset=500`)
          .then((res) => res.data),
      {
        keepPreviousData: true
      }
    )

    return { data, error }
  }

  const getPaginated = (pageParam: number) => {
    const { data, error } = useQuery(
      'pokemons/paginated',
      () =>
        api
          .get<PaginatedResponse>(`/pokemon/?limit=20&offset=${20 * page}`)
          .then((res) => res.data),
      {
        keepPreviousData: true,
        onSuccess: (res) => {
          if (!allData) {
            setAll([...allData, ...res.results])
          }
          setPaginated(res)
        }
      }
    )
  }

  const nextPage = () => setPage(page + 1)
  const previousPage = () => setPage(page + 1)

  return {
    data: {
      allData,
      paginated: {
        paginatedData,
        page
      }
    },
    actions: {
      paginated: {
        getPaginated,
        nextPage,
        previousPage
      },

      all: {
        getAll
      }
    }
  } as QueryPokemonsTypes
}
