import React, { createContext, useContext, useMemo } from 'react'
import { queryPokemons } from '../../hooks/query'
import {
  PaginatedResponse,
  QueryPokemonsTypes,
  ReturnOBJ
} from '../../hooks/query/queryPokemons'

interface PokemonsContextTypes {
  paginated: {
    data: {
      paginatedData?: PaginatedResponse
      page: number
    }
    actions: {
      getPaginated: (pageParam?: number | undefined) => void
      nextPage: () => void
      previousPage: () => void
    }
  }
  all: {
    data?: ReturnOBJ[]
    actions: {
      getAll: () => void
    }
  }
}
const PokemonsContext = createContext({} as QueryPokemonsTypes)

export const usePokemons = () => useContext(PokemonsContext)
export const PokemonContextWrapper: React.FC = ({ children }) => {
  const { actions, data } = queryPokemons()

  const runPaginated = () => {
    actions.paginated.getPaginated()
    return {
      data: data.paginated,
      actions: actions.paginated
    }
  }

  const runAll = () => {
    actions.paginated.getPaginated()
    return {
      data: data.allData,
      actions: actions.all
    }
  }

  const ctxValue: QueryPokemonsTypes = useMemo(
    () => ({
      paginated: runPaginated,
      all: runAll
    }),
    [actions, data]
  )

  return (
    <PokemonsContext.Provider value={ctxValue}>
      {children}
    </PokemonsContext.Provider>
  )
}
