import React, { useState } from 'react'
import { useQuery } from 'react-query'
import { api } from '../../service/api'

export type ReturnOBJ = {
  name: string
  url: string
}

export interface PaginatedResponse {
  count: number
  next?: string
  previous?: string
  results: ReturnOBJ[]
}

export interface QueryPokemonsTypes {
  data: {
    allData?: ReturnOBJ[]
    paginated: {
      paginatedData?: PaginatedResponse
      page: number
    }
  }
  actions: {
    paginated: {
      getPaginated: (pageParam?: number) => void
      nextPage: () => void
      previousPage: () => void
    }

    all: {
      getAll: () => void
    }
  }
}

type QueryPokemonsProps = {
  setPage: () => void
  page: number
  setPaginated: (data: PaginatedResponse) => void
  setAll: (data: ReturnOBJ[]) => void
  allData: ReturnOBJ[] | []
}

export const queryPokemons = ({
  allData,
  page,
  setAll,
  setPage,
  setPaginated
}: QueryPokemonsProps) => {
  const getAll = () => {
    const { data, error } = useQuery(
      'pokemons/all',
      () =>
        api
          .get<PaginatedResponse>(`/pokemon/?limit=500&offset=500`)
          .then((res) => res.data),
      {
        keepPreviousData: true
      }
    )
    if (error || !data) return false
    return setAll(data?.results)
  }

  const getPaginated = (pageParam: number = page) => {
    const { data, error } = useQuery(
      'pokemons/paginated',
      () =>
        api
          .get<PaginatedResponse>(`/pokemon/?limit=20&offset=${20 * page}`)
          .then((res) => res.data),
      {
        keepPreviousData: true
      }
    )
    if (error || !data) return false

    if (!allData) {
      setAll([...allData, ...data.results])
    }
    return setPaginated(data)
  }

  const nextPage = () => setPage(page + 1)
  const previousPage = () => setPage(page + 1)

  return {
    data: {
      allData,
      paginated: {
        paginatedData,
        page
      }
    },
    actions: {
      paginated: {
        getPaginated,
        nextPage,
        previousPage
      },

      all: {
        getAll
      }
    }
  } as QueryPokemonsTypes
}
