import React, { useState } from 'react'
import { useQuery } from 'react-query'
import { api } from '../../service/api'

export type ReturnOBJ = {
  name: string
  url: string
}

export interface PaginatedResponse {
  count: number
  next?: string
  previous?: string
  results: ReturnOBJ[]
}

export type QueryResponse<T> = {
  data: T | undefined
  error: unknown
}
export interface QueryPokemonsTypes {
  getPaginated: (pageParam?: number) => QueryResponse<PaginatedResponse>
  getAll: () => QueryResponse<ReturnOBJ[]>
}

export const queryPokemons = () => {
  const getAll = () => {
    const { data, error } = useQuery(
      'pokemons/all',
      () =>
        api
          .get<PaginatedResponse>(`/pokemon/?limit=500&offset=500`)
          .then((res) => res.data),
      {
        keepPreviousData: true
      }
    )

    return { data, error }
  }

  const getPaginated = (pageParam: number) => {
    const { data, error } = useQuery(
      'pokemons/paginated',
      () =>
        api
          .get<PaginatedResponse>(`/pokemon/?limit=20&offset=${20 * pageParam}`)
          .then((res) => res.data),
      {
        keepPreviousData: true
      }
    )
    return { data, error }
  }

  return {
    getPaginated,
    getAll
  } as QueryPokemonsTypes
}
